import React, { Component } from 'react';
import SearchBar from './components/search_bar';

class App extends Component {
  constructor () {
    super()

    this.state = {
      selectedVideo: []
    }
  }

  searchPlanetByName = (event) => {
    fetch('https://swapi.co/api/planets/'+ event.target.value)
      .then(response => response.json())
      .then(response => {
        //let searchResult = JSON.parse(responseBody).results;
        console.log(response);
        this.setState({ searchResult: response.results });
      })

  }

  render() {
    return (
      <div className="pageStyle">
        <div className="searchBar">

          <input type="text"
            placeholder="Search for a planet"
            onChange={this.searchPlanetByName}>
          </input>
          {Object.keys(this.state.searchResult).map((item, i) => (
            <li key={i}>
              <span>{this.state.searchResult[item].name}</span>
            </li>
          ))}
           <SearchBar onSearchTermChange={this.searchPlanetByName} />
        </div>

      </div>
    );
  }
}

export default App;
