import { Link } from 'react-router-dom'
import React from "react";
import _ from 'lodash';


const PlanetDetail = ({props, planets}) => {
  const planet = _.find(planets, { 'name': props.match.params.name });

  if (!planet) {
    return <div>Sorry But the Planet was not found<Link to='/'> Back</Link></div>;   
  }
  
  return (
      <div className="details">
        <h1>Name: {planet.name}</h1>    
        <h2>Climate: {planet.climate}</h2>
        <h3>Population: {planet.population}</h3>
        <h3>Terrain: {planet.terrain}</h3>
        <h4>Diameter: {planet.diameter}</h4>
        <h5>Surface Water: {planet.surface_water}</h5>
       {/* Handle Submit Click is going to be needed here.  */}
        <Link to='/'><button type="button">Add Planet to Travel List</button></Link>
        <Link to='/'>Roster</Link>
      </div>

  )
};


export default PlanetDetail;
