import 'bootstrap/dist/css/bootstrap.min.css';
import React, { Component } from "react";

class SearchBar extends Component {
  // constructor(props) {
  //   super(props);
  //   console.log(props);

  //    //this.state = { planets: '' };
  // }

  searchPlanetByName = (event) => {
      fetch('https://swapi.co/api/planets/?search='+ event.target.value)
          .then(response => response.json())
          .then(response => {
          //let searchResult = JSON.parse(responseBody).results;
          console.log(props);
          console.log(response);
          this.setState({ planets: response.results });
          console.log(this.state.planets);
          })  
  }

  render() {
    return (
      <div className="search-bar">
        <input type="text"
            placeholder="search for a planet"
            onChange={this.searchPlanetByName}>
          </input>     
      </div>     
    );
  }

  onInputChange(term) {
    this.setState({ term });
    this.props.onSearchTermChange(term);
  }
}

export default SearchBar;
