import { Switch, Route} from 'react-router-dom'
import React, { Component } from 'react';
import Routes from './Routes';
import SearchBar from './SearchBar';
const axios = require('axios');

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            planets: [],
            trip: []
        }
        this.addPlanet = this.addPlanet.bind(this);
    }

    searchPlanetByName = (event, props) => {
      fetch('https://swapi.co/api/planets/?search='+ event.target.value)
      .then(response => response.json())
      .then(response => {
        //let searchResult = JSON.parse(responseBody).results;
        console.log(props);
        console.log(response);
        this.setState({ planets: response.results });
        console.log(this.state.planets);
      })  
    
          axios.get(url, { params: params })
              .then(response => {
                this.setState({
                  videos: response.data.items,
                  selectedVideo: response.data.items[0]
                });
              })
            .catch(error => {
              console.error(error);
              });
      
  }
   

  addPlanet (planet) {
    this.setState({planets: this.state.planets.concat([planet])});
  }


  
render() {
    return (
      <div className="pageStyle">
       <h1>Planet</h1>
       <SearchBar onSearchTermChange={this.searchPlanetByName} />
        <div>
          {Object.keys(this.state.planets).map((item, i) => (
            <ol key={i}>
             <span><h1>{this.state.planets[item].name}</h1></span>
              <ul>
              <h2>Climate: {this.state.planets[item].climate}</h2>
              </ul>
              <ul>
              <h2>Population: {this.state.planets[item].population}</h2>
              </ul>
              <ul>
              <h2>Terrain: {this.state.planets[item].terrain}</h2>
              </ul>
              <ul>
              <h2>Diameter: {this.state.planets[item].diameter}</h2>
              </ul>
              <ul>
              <h2>Surface Water: {this.state.planets[item].surface_water}</h2>
              </ul>
              <button type="button" onClick={this.handleSubmitPlanetClick}>Add Planet to Travel List</button>
            </ol>
          ))}
         
        </div>
       
        <div>
        <Switch>
          <Route exact path='/' render={() => (
            <Routes addPlanet={this.addPlanet} planets={this.state.planets} />
          )}/>
          <Route path='/planet' render={() => (
            <Routes addPlanet={this.addPlanet} planets={this.state.planets} />
          )}/>     
        </Switch>      
      </div>
      </div>
    );
  }
} 
export default App;
