import { Link } from 'react-router-dom'
import React from "react";
import _ from 'lodash';
import PropTypes from 'prop-types';


const PlanetDetail = (props, planets) => {
  const planet = _.find(planets, { name: props.match.params.name });

  if (!planet) {
    return <div>Loading...</div>;
  }
  
  return (
      <div className="details">
        <h1>Name: {planet.name}</h1>
    
        <h2>Climate: {planet.climate}</h2>
        <h3>Population: {props.planet.population}</h3>
        <h4>Diameter: {props.planet.diameter}</h4>
        <h5>Surface Water: {props.planet.surface_water}</h5>
        <Link to='/'>Roster</Link>
      </div>

  )
};

PlanetDetail.propTypes = {
  name: PropTypes.number.isRequired
}

export default PlanetDetail;
