import { Switch } from 'react-router-dom'
import React, { Component } from 'react';

import ReactDOM from 'react-dom';
import SearchBar from './components/search_bar'



class App extends Component {
  constructor () {
    super()

    this.state = {
      searchResult: [],
    }
  }

  searchPlanetByName = (event) => {
    fetch('https://swapi.co/api/planets/?search='+ event.target.value)
      .then(response => response.json())
      .then(response => {
        //let searchResult = JSON.parse(responseBody).results;
        console.log(response);
        this.setState({ searchResult: response.results });
      })

  }

  render() {
    return (
      <div className="pageStyle">
        <div className="searchBar">

          <input type="text"
            placeholder="Search for a planet"
            onChange={this.searchPlanetByName}>

          </input>

         <button>SearchBar</button>

        </div>
        <div>
          <Switch>
            <SearchBar onSearchTermChange={this.videoSearch} />
           </Switch>
          </div>

      </div>
    );
  }
}

ReactDOM.render(<App />, document.getElementById('root'));
