import 'bootstrap/dist/css/bootstrap.min.css';
import React, { Component } from "react";
import ReactDOM from 'react-dom';
import SearchBar from "./components/search_bar";
import PlanetDetail from "./components/planet_detail";
import PlanetList from "./components/planet_list";
const axios = require('axios');

class App extends Component {
    constructor () {
      super()

      this.state = {
        videos: [],
        selectedVideo: null
      };   
    }

    planetSearch = (term) => { 
      this.setState({videos : [], selectedVideo: null })
        console.log(term);
        const url = 'https://www.googleapis.com/youtube/v3/search';

           const params =  {
                search: term
            };

        axios.get(url, { params: params })
            .then(response => {
              this.setState({
                planets: response.data.items,
                selectedPlanet: response.data.items[0]
              });
            })
          .catch(error => {
            console.error(error);
            });
    }

    render () {
        return (
          <div>
            <SearchBar onSearchTermChange={this.planetSearch} />
            <PlanetDetail video={this.state.selectedPlanet} />
            <PlanetList videos={this.state.planets} />
          </div>
        )
      }
  }

ReactDOM.render(<App />, document.getElementById('root'));
