import { Switch, Route, Link} from 'react-router-dom'
import React, { Component } from 'react';
import Routes from './Routes';
import SearchBar from './SearchBar';
import PlanetDetail from './planet_detail';
import AllPlanets from './AllPlanets';


class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            planets: [],
            trip: []
        }
        this.addPlanet = this.addPlanet.bind(this);
        this.SelectPlanet = this.SelectPlanet.bind(this);
    }

  addPlanet (planet) {
    this.setState({planets: this.state.planets.concat([planet])});
  }

  SelectPlanet (planet) {
    this.setState({trip: this.state.trip.concat([planet])});
  }

  searchPlanetByName = (event) => {
    fetch('https://swapi.co/api/planets/?search='+ event.target.value)
    .then(response => response.json())
    .then(response => {
      //let searchResult = JSON.parse(responseBody).results;
      console.log(response);
      this.setState({ planets: response.results });
      console.log(this.state.planets);
    })  
}
  
render() {
    return (
      <div className="pageStyle">
       <h1>Planet</h1>
       <SearchBar SearchPlanets={this.searchPlanetByName} />
       <AllPlanets planets={this.state.planets} />
       <PlanetDetail planet={this.state.selectedPlanet} />       
        <div>
          {Object.keys(this.state.planets).map((item, i) => (
            <ol key={i}>
             <span><Link to={`/planet/${p.name}`}>{p.name}</Link></span>
              <ul>
              
              </ul>
              <ul>
              <h2>Population: {this.state.planets[item].population}</h2>
              </ul>
              <ul>
              <h2>Terrain: {this.state.planets[item].terrain}</h2>
              </ul>
              <ul>
              <h2>Diameter: {this.state.planets[item].diameter}</h2>
              </ul>
              <ul>
              <h2>Surface Water: {this.state.planets[item].surface_water}</h2>
              </ul>
              <Link to='/'><button type="button" onClick={this.handleSubmitPlanetClick}>Add Planet to Travel List</button></Link>
            </ol>
          ))}
         
        </div>
       
        <div>
        <Switch>
          <Route exact path='/' render={() => (
            <Routes addPlanet={this.addPlanet} planets={this.state.planets} />
          )}/>
          <Route path='/planet' render={() => (
            <Routes addPlanet={this.addPlanet} planets={this.state.planets} />
          )}/>     
        </Switch>      
      </div>
     
      </div>
    );
  }
} 
export default App;
