import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from 'react-router-dom'
import React, { Component } from "react";

class SearchBar extends Component {
  constructor(props) {
    super(props);

    this.state = { name: '' };
  }

  searchPlanetByName = (event) => {
    fetch('https://swapi.co/api/planets/?search='+ event.target.value)
        .then(response => response.json())
        .then(response => {
        //let searchResult = JSON.parse(responseBody).results;
        console.log(response);
        this.setState({ planets: response.results });
        })
    }

  render() {
    return (
      <div className="pageStyle">
      <div className="searchBar">
      <input type="text"
        placeholder="search for a planet"
        onChange={this.searchPlanetByName}>
      </input>
      {Object.keys(this.state.planets).map((item, i) => (
        <ol key={i}>
         <span><h1>{this.state.planets[item].name}</h1></span>
          <ul>
          <h2>Climate: {this.state.planets[item].climate}</h2>
          </ul>
          <ul>
          <h2>Population: {this.state.planets[item].population}</h2>
          </ul>
          <ul>
          <h2>Terrain: {this.state.planets[item].terrain}</h2>
          </ul>
          <ul>
          <h2>Diameter: {this.state.planets[item].diameter}</h2>
          </ul>
          <ul>
          <h2>Surface Water: {this.state.planets[item].surface_water}</h2>
          </ul>
          <Link to='/'><button>Add Planet to Travel List</button></Link>
        </ol>
      ))}
     
    </div>
    </div>
     
    );
  }

  onInputChange(term) {
    this.setState({ term });
    this.props.onSearchTermChange(term);
  }
}

export default SearchBar;
