import React, { Component } from 'react';
import SearchBar from './search_bar'


class App extends Component {
  constructor () {
    super()

    this.state = {
      planets: [
        { name: "Tatooine",
          name: "Geonosis" }
      ]
    }
  }

  searchPlanetByName = (event) => {
    fetch('https://swapi.co/api/planets/?search='+ event.target.value)
      .then(response => response.json())
      .then(response => {
        //let searchResult = JSON.parse(responseBody).results;
        console.log(response);
        this.setState({ planets: response.results });
      })

  }

  render() {
    return (
      <div className="pageStyle">
        <div className="searchBar">
       
          <input type="text"
            placeholder="Search for a planet"
            onChange={this.searchPlanetByName}>
             
          </input>
         
         <button>SearchBar</button>
        
        </div>
        <div>
    
             <SearchBar onSearchTermChange={this.searchPlanetByName} /> 
    
       
          </div>
       
      </div>
    );
  }
}

export default App
