import { Switch, Route } from 'react-router-dom'
import React, { Component } from 'react';
import SearchBar from './search_bar'
import Roster from './Roster'


class App extends Component {
  constructor () {
    super()

    this.state = {
      planets: [
        { name: "Tatooine" },
        { name: "Geonosis" }
      ]
    }
  }

  searchPlanetByName = (event) => {
    fetch('https://swapi.co/api/planets/?search='+ event.target.value)
      .then(response => response.json())
      .then(response => {
        //let searchResult = JSON.parse(responseBody).results;
        console.log(response);
        this.setState({ planets: response.results });
      })

  }

  render() {
    return (
      <div className="pageStyle">
        <div className="searchBar">
       
          <input type="text"
            placeholder="Search for a planet"
            onChange={this.searchPlanetByName}>
             
          </input>
         
         <button>SearchBar</button>
        
        </div>
        <div>
             <SearchBar onSearchTermChange={this.searchPlanetByName} />
        </div>
        <div>
        <Switch>
          
          <Route path='/roster' render={() => (
            <Roster players={this.state.players} />
          )}/>
         
        </Switch>
      </div>
       
      </div>
    );
  }
}

export default App
