import React, { Component } from 'react';

class App extends Component {
  constructor() {
    super();
    this.state = {
      searchResult: [],
    }
  }

    searchPersonByName = (event) => {
    fetch('https://swapi.co/api/planets/?search='+ event.target.value)
      .then(response => response.json())
      .then(response => {
        //let searchResult = JSON.parse(responseBody).results;
        console.log(response);
        this.setState({ searchResult: response.results });
      })
  }


render() {
    return (
      <div className="pageStyle">
        <div className="searchBar">
          <input type="text"
            placeholder="search for a planet"
            onChange={this.searchPersonByName}>
          </input>
          <button>Search</button>
          {Object.keys(this.state.searchResult).map((item, i) => (
            <ol key={i}>
             <span><h1>{this.state.searchResult[item].name}</h1></span>
              <ul>
              <h2>Climate: {this.state.searchResult[item].climate}</h2>
              </ul>
              <ul>
              <h2>Population: {this.state.searchResult[item].population}</h2>
              </ul>
              <ul>
              <h2>Terrain: {this.state.searchResult[item].terrain}</h2>
              </ul>
              <ul>
              <h2>Diameter: {this.state.searchResult[item].diameter}</h2>
              </ul>
              <ul>
              <h2>Surface Water: {this.state.searchResult[item].surface_water}</h2>
              </ul>
              <button>Add Planet to Travel List</button>
            </ol>
          ))}
         
        </div>
      </div>
    );
  }
}

export default App;
