import 'bootstrap/dist/css/bootstrap.min.css';
import React, { Component } from "react";
import ReactDOM from 'react-dom';
import SearchBar from "./components/search_bar";
import PlanetDetail from "./components/planet_detail";
import PlanetList from "./components/planet_list";
const API_KEY = "AIzaSyDan5m7YffS-93Vb_8EzwBONvaGqtkmHJo";
const axios = require('axios');

class App extends Component {
    constructor () {
      super()

      this.state = {
        videos: [],
        selectedVideo: null
      };   
    }

    videoSearch = (term) => { 
      this.setState({videos : [], selectedVideo: null })
        console.log(term);
        const url = 'https://www.googleapis.com/youtube/v3/search';

           const params =  {
                part: 'snippet',
                key: API_KEY,
                q: term,
                type: 'video'
            };

        axios.get(url, { params: params })
            .then(response => {
              this.setState({
                videos: response.data.items,
                selectedVideo: response.data.items[0]
              });
            })
          .catch(error => {
            console.error(error);
            });
    }

    render () {
        return (
          <div>
            <SearchBar onSearchTermChange={this.videoSearch} />
            <PlanetDetail video={this.state.selectedVideo} />
            <PlanetList videos={this.state.videos} />
          </div>
        )
      }
  }

ReactDOM.render(<App />, document.getElementById('root'));
